mapper.select:
select d.rec_uid recUid, d.corp_id corpId, c.corp_name corpName, d.project_name projectName, d.contract_no contractNo, d.rel_sale_corp_name relSaleCorpName, d.is_recourse isRecourse, d.factor_type factorType, d.create_user_name createUserName, DATE_FORMAT(d.start_date,'%Y-%m-%d') startDate, d.buyer_id buyerId, d.seller_id sellerId, d.contacts, d.contacts1, d.contacts2, d.telephone_number telephoneNumber, d.telephone_number1 telephoneNumber1, d.telephone_number2 telephoneNumber2, d.address, d.note, d.operation_note operationNote, d.operation_note1 operationNote1, d.operation_note2 operationNote2, return_visit_material returnVisitMaterial, return_visit_material1 returnVisitMaterial1, return_visit_material2 returnVisitMaterial2 from dun_management d join corp_info c on d.corp_id=c.corp_id where 1=1 and c.status = 1
and d.corp_id = #{corpId}
and d.project_name LIKE CONCAT('%',#{projectName},'%')
and d.contract_no LIKE CONCAT('%',#{contractNo},'%')
and d.create_user_id = #{createUserId}
order by d.create_time desc
