mapper.select:
select r.rec_uid recUid, r.corp_id corpId, c.corp_name corpName, r.project_name projectName, r.contract_no contractNo, r.balance_account balanceAccount, r.expense_type expenseType, r.pay_amount payAmount, r.actual_amount actualAmount, r.should_repay_amount shouldRepayAmount, r.create_time createTime from reconciliation_management r join corp_info c on r.corp_id=c.corp_id
r.status = 1 and c.status = 1
and r.project_name LIKE CONCAT(CONCAT('%', #{projectName}), '%')
and r.corp_id = #{corpId}
and r.corp_id in ${userCorpList}
and r.contract_no LIKE CONCAT(CONCAT('%', #{contractNo}), '%')
and r.create_user_id = #{createUserId}
order by r.create_time desc

  	select
	  count(1)
    from reconciliation_management
    where project_name = #{projectName}
      and expense_type = #{expenseType}
      and corp_id = #{corpId}

mapper.update:
update reconciliation_management


    status = #{status},


where project_name = #{projectName} and corp_id = #{corpId}
