mapper.select:
select contract_no contractNo,sign_date signDate,contract_val_date contractValDate, contract_due_date contractDueDate,contract_type contractType,order_batch_id orderBatchId, product_type productType,core_corp_id coreCorpId, agency_corp_id agencyCorpId from factor_contract_info
1=1
and sign_date = #{signDate}
