mapper.select:
SELECT fi.finance_id financeId, fi.apply_date applyDate, ci.corp_name agencyName, ci.agency_num agencyNum, li.max_credit_amount maxCredit, li.use_able_credit_amt availableCredit, fi.finance_rate financeRate, fi.cash_rate cashRate, fi.finance_start_date financeStartDate, fi.finance_end_date financeEndDate, fi.pay_amt payAmt, fi.finance_balance financeBalance, fi.finance_amount financeAmount, fi.expense expense, fi.finance_status financeStatus from finance_info fi join corp_info ci on fi.corp_id = ci.corp_id left join limit_info li on ci.corp_id = li.corp_id WHERE 1 = 1
and ci.corp_name LIKE CONCAT(CONCAT('%', #{agencyName}),'%')
and ci.agency_num LIKE CONCAT(CONCAT('%', #{agencyNum}),'%')
and fi.finance_id LIKE CONCAT(CONCAT('%', #{financeId}),'%')
and DATE_FORMAT(fi.finance_start_date, '%Y-%m-%d') >= #{financeStartDate}
and DATE_FORMAT(fi.finance_end_date, '%Y-%m-%d') <= #{financeEndDate}
and fi.finance_status = #{financeStatus}

SELECT ci.corp_name agencyName, ci.agency_num agencyNum, SUM(fi.finance_amount) financeAmountSum, SUM(fi.pay_amt) payAmtSum, SUM(fi.finance_balance) financeBalanceSum, (SUM(fi.finance_amount)-SUM(fi.finance_balance)) financeAlreadySum, SUM(if(fi.finance_status = 5, fi.finance_amount, 0)) overdueFinanceAmountSum, SUM(if(fi.finance_status = 1, fi.finance_amount, 0)) toBeFinanceAmountSum from finance_info fi join corp_info ci on fi.corp_id = ci.corp_id WHERE 1 = 1
and ci.corp_name LIKE CONCAT(CONCAT('%', #{agencyName}),'%')
and ci.agency_num LIKE CONCAT(CONCAT('%', #{agencyNum}),'%')
and DATE_FORMAT(fi.finance_start_date, '%Y-%m-%d') >= #{financeStartDate}
and DATE_FORMAT(fi.finance_end_date, '%Y-%m-%d') <= #{financeEndDate}
and fi.finance_status = #{financeStatus}
GROUP BY ci.corp_name
