select cc.rec_uid recUid, cc.corp_id corpId, cc.oper_year operYear, c.corp_name corpName, cc.current_rate_analysis currentRateAnalysis, cc.quick_rate_analysis quickRateAnalysis, cc.interest_coverage_analysis interestCoverageAnalysis, cc.cash_rate_analysis cashRateAnalysis, cc.assets_and_liabilities_analysis assetsAndLiabilitiesAnalysis, cc.equity_rate_analysis equityRateAnalysis, cc.receivable_turnover_rate_analysis receivableTurnoverRateAnalysis, cc.receivable_turnover_rate_days_analysis receivableTurnoverRateDaysAnalysis, cc.inventory_turnover_analysis inventoryTurnoverAnalysis, cc.inventory_turnover_days_analysis inventoryTurnoverDaysAnalysis, cc.current_assets_turnover_analysis currentAssetsTurnoverAnalysis, cc.current_assets_turnover_days_analysis currentAssetsTurnoverDaysAnalysis, cc.fixed_asset_turnover_analysis fixedAssetsTurnoverAnalysis, cc.fixed_asset_turnover_days_analysis fixedAssetsTurnoverDaysAnalysis, cc.total_asset_turnover_analysis totalAssetTurnoverAnalysis, cc.total_asset_turnover_days_analysis totalAssetTurnoverDaysAnalysis, cc.accounts_payable_turnover_analysis accountsPayableTurnoverAnalysis, cc.accounts_payable_turnover_days_analysis accountsPayableTurnoverDaysAnalysis, cc.cash_turnover_analysis cashTurnoverAnalysis, cc.cash_turnover_days_analysis cashTurnoverDaysAnalysis, cc.operating_margin_analysis operatingMarginAnalysis, cc.operating_net_profit_analysis operatingNetProfitAnalysis, cc.operating_gross_profit_analysis operatingGrossProfitAnalysis, cc.cost_margins_analysis costMarginsAnalysis, cc.cost_rate_analysis costRateAnalysis, cc.return_total_assets_analysis returnTotalAssetsAnalysis, cc.return_net_assets_analysis returnNetAssetsAnalysis, cc.main_business_revenue_growth_analysis mainBusinessRevenueGrowthAnalysis, cc.main_business_profit_growth_analysis mainBusinessProfitGrowthAnalysis, cc.net_profit_growth_analysis netProfitGrowthAnalysis, cc.total_asset_growth_analysis totalAssetGrowthAnalysis, cc.capital_maintenance_and_appreciation_analysis capitalMaintenanceAndAppreciationAnalysis, cc.cash_of_sales_analysis cashOfSalesAnalysis, cc.cash_of_purchase_analysis cashOfPurchaseAnalysis, cc.net_operating_cash_to_sales_analysis netOperatingCashToSalesAnalysis, cc.operating_cash_rate_of_return_analysis operatingCashRateOfReturnAnalysis, cc.net_operating_cash_to_profit_analysis netOperatingCashToProfitAnalysis, cc.net_operating_cash_to_debt_analysis netOperatingCashToDebtAnalysis, cc.proportion_of_fixed_assets_analysis proportionOfFixedAssetsAnalysis, cc.shareholder_rate_analysis shareholderRateAnalysis, cc.longterm_debt_rate_analysis longtermDebtRateAnalysis, cc.fixed_assets_and_equity_rate_analysis fixedAssetsAndEquityRateAnalysis, cc.main_business_profit_rate_analysis mainBusinessProfitRateAnalysis, cc.accounts_receivable_income_rate_analysis accountsReceivableIncomeRateAnalysis, cc.liquidity_income_rate_analysis liquidityIncomeRateAnalysis, cc.total_assets_profit_margin_analysis totalAssetsProfitMarginAnalysis, cc.loan_recovery_during_reporting_period_analysis loanRecoveryDuringReportingPeriodAnalysis
from corp_comprehensive_eval_contrast_analysis cc JOIN corp_info c ON cc.corp_id = c.corp_id where 1=1 and c.status=1
and cc.corp_id = #{corpId}
and cc.oper_year = #{operYear}
and cc.corp_id in ${userCorpList}
and cc.rela_corp_id = #{relaCorpId}
and cc.create_user_id = #{createUserId}

    select cca.rec_uid recUid
    from corp_comprehensive_eval_contrast_analysis cca

        1=1

            and cca.oper_year = #{operYear}


		    and cca.corp_id = #{corpId}


	  		and cca.rela_corp_id = #{relaCorpId}


	  		and cca.create_user_id = #{createUserId}


    limit 1
