select ni.neg_id negId, ni.rela_corp_id relaCorpId, ni.corp_id corpId, ni.oper_year operYear, ni.money_funds moneyFunds, ni.other_money_funds otherMoneyFunds, ni.transactional_financial_assets transactionalFinancialAssets, ni.bill_receivable billReceivable, ni.accounts_receivable accountsReceivable, ni.prepayments prepayments, ni.interest_receivable interestReceivable, ni.dividend_receivable dividendReceivable, ni.other_receivables otherReceivables, ni.stock stock, ni.non_current_assets_due_within_one_year nonCurrentAssetsDueWithinOneYear, ni.other_current_assets otherCurrentAssets, ni.total_current_assets totalCurrentAssets, ni.available_for_sale_financial_assets availableForSaleFinancialAssets, ni.held_to_maturity_investments heldToMaturityInvestments, ni.long_term_receivables longTermReceivables, ni.long_term_equity_investment longTermEquityInvestment,ni.investment_real_estate investmentRealEstate, ni.fixed_assets fixedAssets, ni.construction_in_progress constructionInProgress, ni.engineer_material engineerMaterial, ni.clean_up_of_fixed_assets cleanUpOfFixedAssets, ni.productive_biological_assets productiveBiologicalAssets, ni.oil_and_gas_properties oilAndGasProperties, ni.intangible_assets intangibleAssets, ni.development_expenditure developmentExpenditure,ni.goodwill goodwill, ni.long_term_prepaid_expenses longTermPrepaidExpenses, ni.deferred_tax_assets deferredTaxAssets, ni.other_non_current_assets otherNonCurrentAssets, ni.total_non_current_assets totalNonCurrentAssets, ni.total_assets totalAssets, ni.short_term_loan shortTermLoan, ni.transactional_financial_liabilities transactionalFinancialLiabilities, ni.notes_payable notesPayable, ni.accounts_payable accountsPayable, ni.advance_payment advancePayment, ni.employee_benefits_payable employeeBenefitsPayable, ni.taxes_payable taxesPayable, ni.interest_payable interestPayable, ni.dividend_payable dividendPayable, ni.other_payables otherPayables, ni.non_current_liabilities_due_within_one_year nonCurrentLiabilitiesDueWithinOneYear, ni.other_current_liabilities otherCurrentLiabilities, ni.total_current_liabilities totalCurrentLiabilities, ni.long_term_loan longTermLoan, ni.bonds_payable bondsPayable, ni.long_term_payables longTermPayables, ni.special_payables specialPayables, ni.projected_liabilities projectedLiabilities, ni.deferred_income_tax_liabilities deferredIncomeTaxLiabilities, ni.other_non_current_liabilities otherNonCurrentLiabilities, ni.total_non_current_liabilities totalNonCurrentLiabilities, ni.total_liabilities totalLiabilities, ni.paid_up_capital paidUpCapital, ni.capital_reserve capitalReserve, ni.treasury_stocks treasuryStocks, ni.surplus_reserve surplusReserve, ni.undistributed_profit undistributedProfit, ni.total_owners_equity totalOwnersEquity, ni.total_liabilities_and_owners_equity totalLiabilitiesAndOwnersEquity, ni.average_current_assets averageCurrentAssets, ni.average_current_liabilities averageCurrentLiabilities, ni.average_total_assets averageTotalAssets, ni.average_total_liabilities averageTotalLiabilities, ni.average_net_assets averageNetAssets, ni.average_net_fixed_assets averageNetFixedAssets, ni.average_accounts_receivable averageAccountsReceivable, ni.average_accounts_payable averageAccountsPayable, ni.average_inventory averageInventory, ni.average_prepayment averagePrepayment, ni.average_other_receivables averageOtherReceivables,ni.total_assets_growth totalAssetsGrowth, ni.total_assets_growth_rate totalAssetsGrowthRate, ni.average_money_funds averageMoneyFunds, ni.unallocated_profit_spreadsheet unallocatedProfitSpreadsheet, ni.balance_of_undistributed_profit balanceOfUndistributedProfit, ni.increase_in_surplus_funds increaseInSurplusFunds, ni.increase_in_net_assets increaseInNetAssets, ni.net_assets_growth_rate netAssetsGrowthRate,
ci.corp_name corpName from negative_info ni join corp_info ci on ci.corp_id=ni.corp_id
1=1 and ci.status = 1
and ci.corp_name LIKE CONCAT(CONCAT('%', #{corpName}),'%')
and ni.corp_id = #{corpId}
and ni.corp_id in ${userCorpList}
and ni.rela_corp_id = #{relaCorpId}
and ni.create_user_id = #{createUserId}
order by ni.create_time desc

select ni.total_liabilities_and_owners_equity totalLiabilitiesAndOwnersEquity, ni.undistributed_profit undistributedProfit, ni.surplus_reserve surplusReserve, ni.total_owners_equity totalOwnersEquity from negative_info ni
1 =1
and ni.oper_year = #{operYear}
and ni.corp_id = #{corpId}
LIMIT 1 UNION select ni.total_liabilities_and_owners_equity totalLiabilitiesAndOwnersEquity, ni.undistributed_profit undistributedProfit, ni.surplus_reserve surplusReserve, ni.total_owners_equity totalOwnersEquity from negative_info ni
1=1
and ni.oper_year = (CAST(#{operYear} AS DECIMAL)+1)
and ni.corp_id = #{corpId}
LIMIT 1

    select count(1)
    from negative_info ni

        1=1

            and ni.oper_year = #{operYear}


		    and ni.corp_id = #{corpId}


	  		and ni.rela_corp_id = #{relaCorpId}


	  		and ni.create_user_id = #{createUserId}
