mapper.select:
select m.rec_uid recUid, m.corp_id corpId, c.corp_name corpName, m.project_name projectName, m.collection_company collectionCompany, DATE_FORMAT(m.collection_date,'%Y-%m-%d') collectionDate, m.payee, m.collection_company_account collectionCompanyAccount, m.collection_amount collectionAmount, m.source_description sourceDescription, m.contract_no contractNo, m.collection_type collectionType, m.collection_plan collectionPlan from collection_management m join corp_info c on m.corp_id=c.corp_id
1=1 and c.status = 1
and m.project_name LIKE CONCAT(CONCAT('%', #{projectName}), '%')
and m.corp_id = #{corpId}
and m.corp_id in ${userCorpList}
and m.contract_no LIKE CONCAT(CONCAT('%', #{contractNo}), '%')
and m.rela_corp_id = #{relaCorpId}
and m.create_user_id = #{createUserId}
order by m.create_time desc
